import React, { useState, useEffect } from 'react';
import { useTransition, animated } from 'react-spring';

function FunnelChart(props) {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [width, setWidth] = useState(0);
  const [height, setHeight] = useState(0);

  useEffect(() => {
    setWidth(props.width || 400);
    setHeight(props.height || 300);
  }, [props.width, props.height]);

  const data = props.data || [];

  const xScale = (value) => {
    return value;
  };

  const yScale = (value, index) => {
    return index;
  };

  const transitions = useTransition(
    data.length ? data.map((entry, index) => ({
      y1: yScale(entry[1], index),
      y2: yScale(entry[0], index),
      color: entry[2],
      index,
    })) : [],
    (d) => d.index,
    {
      from: {
        y1: height,
        y2: height,
      },
      enter: (d) => ({
        y1: d.y1,
        y2: d.y2,
        color: d.color,
      }),
      update: (d) => ({
        y1: d.y1,
        y2: d.y2,
        color: d.color,
      }),
      leave: {
        y1: height,
        y2: height,
      },
    }
  );

  const handleMouseEnter = (index) => {
    setHoveredIndex(index);
  };

  const handleMouseLeave = () => {
    setHoveredIndex(null);
  };

  const segmentWidth = width / 2;

  return (
    <svg viewBox={`0 0 ${width} ${height}`} style={{ border: '1px solid black' }}>
      {transitions.map(({ item, props, key }, index) => {
        const { y1, y2, color } = item;
        const x = segmentWidth * (index % 2);
        const width = segmentWidth;
        return (
          <animated.rect
            key={key}
            x={x}
            y={props.y1.interpolate((v) => v)}
            width={width}
            height={props.y2.interpolate((v) => v - v * 0.1)}
            fill={color}
            onMouseEnter={() => handleMouseEnter(index)}
            onMouseLeave={handleMouseLeave}
          />
        );
      })}
      {hoveredIndex !== null && (
        <text x={width / 2} y={height / 2}>
          {data[hoveredIndex][1]}
        </text>
      )}
    </svg>
  );
}

export default FunnelChart;
