import * as d3 from "d3";

/**
 * Draw the x-axis on the chart
 * @param {*} svg 
 * @param {*} chartHeight 
 * @param {*} xAxis 
 */
export function drawXAxis(svg, chartHeight, xAxis) {
  svg
    .select(".x-axis")
    .attr("transform", `translate(0, ${chartHeight})`)
    .call(xAxis);
}

/**
 * Draw the y-axis on the chart
 * @param {*} svg 
 * @param {*} yAxis 
 */
export function drawYAxis(svg, yAxis) {
  svg.select(".y-axis").call(yAxis);
}

/**
 * Draw the bars on the chart
 * @param {*} svg 
 * @param {*} data 
 * @param {*} x 
 * @param {*} y 
 * @param {*} chartHeight 
 */
export function drawBars(svg, data, x, y, chartHeight) {
  svg
    .selectAll(".bar")
    .data(data)
    .join("rect")
    .attr("class", "bar")
    .attr("x", (d) => x(new Date(d.Year).getFullYear()))
    .attr("y", (d) => y(d.Population))
    .attr("width", x.bandwidth())
    .attr("height", (d) => chartHeight - y(d.Population));
}
