import "../App.css";
import { useState, useEffect } from "react";
import Chart from "./Chart";

function App() {
  const [population, setPopulation] = useState([]);
  const [yearFilter, setYearFilter] = useState(null);

  async function fetchPopulation() {
    const response = await fetch(
      "https://datausa.io/api/data?drilldowns=Nation&measures=Population"
    );
    const data = await response.json();
    const populationData = data.data.map((item) => ({
      Year: item.Year,
      Population: item.Population,
    }));
    setPopulation(populationData);
  }

  useEffect(() => {
    fetchPopulation();
  }, []);

  function handleYearFilterChange(event) {
    setYearFilter(parseInt(event.target.value));
  }

  function getFilteredData() {
    if (!yearFilter) {
      return population;
    }
    const filteredData = population.filter(
      (item) => new Date(item.Year).getFullYear() >= new Date().getFullYear() - yearFilter
    );
    return filteredData;
  }

  return (
    <div className="App">
      <h1>USA Population</h1>
      <label>
        Filter by year:
        <select defaultValue={yearFilter} onChange={handleYearFilterChange}>
          <option value={null}>All</option>
          <option value={3}>Last 3 years</option>
          <option value={5}>Last 5 years</option>
          <option value={10}>Last 10 years</option>
        </select>
      </label>
      <Chart data={getFilteredData()} />
    </div>
  );
}

export default App;
