import "../App.css";
import { useState, useEffect } from "react";
import BarChart from "./Chart/BarChart";

function Body() {
  const [population, setPopulation] = useState([]);
  const [yearFilter, setYearFilter] = useState(null);
  const [userData, setUserData] = useState({
    labels: [],
    datasets: [
      {
        label: "Population",
      data: [],
      backgroundColor: ["black"],
      borderColor: "gray",
      borderWidth: 2,
      pointBackgroundColor: "black",
      pointBorderColor: "#FD7751",
      pointBorderWidth: 4,
      pointHoverBackgroundColor: "#FFD866",
      pointHoverBorderColor: "#FFD866",
      pointHoverRadius: 5,
      pointRadius: 3
      },
    ],
  });

  async function fetchPopulation() {
    const response = await fetch(
      "https://datausa.io/api/data?drilldowns=Nation&measures=Population"
    );
    const data = await response.json();
    setPopulation(data.data);
  }

  useEffect(() => {
    fetchPopulation();
  }, []);

  useEffect(() => {
    if (population.length > 0) {
      const filteredPopulation = population.filter(
        (item) =>
          !yearFilter ||
          new Date(item.Year).getFullYear() >=
            new Date().getFullYear() - yearFilter
      );
      const labels = filteredPopulation.map((data) => data.Year);
      const data = filteredPopulation.map((data) => data.Population);

      setUserData((prevState) => {
        return {
          ...prevState,
          labels: labels,
          datasets: [
            {
              ...prevState.datasets[0],
              data: data,
            },
          ],
        };
      });
    }
  }, [yearFilter, population]);

  function handleYearFilterChange(event) {
    setYearFilter(parseInt(event.target.value));
  }

  return (
    <div className="App">
  <h1 className="text-4xl font-bold mb-8">USA Population</h1>
  <label className="relative inline-block">
    <span className="mr-2">Filter by year:</span>
    <select className="block appearance-none w-full bg-white border border-gray-400 hover:border-gray-500 px-4 py-2 pr-8 rounded shadow leading-tight focus:outline-none focus:shadow-outline" defaultValue={10} onChange={handleYearFilterChange}>
      <option value={3}>Last 3 years</option>
      <option value={5}>Last 5 years</option>
      <option value={10}>Last 10 years</option>
    </select>
    <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
      <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M14.95 7.95l-3.95 3.95-3.95-3.95L7.05 9.9l3.95 3.95-3.95 3.95 1.4 1.4 3.95-3.95 3.95 3.95 1.4-1.4-3.95-3.95 3.95-3.95-1.4-1.4z"/></svg>
    </div>
  </label>
  <BarChart chartData={userData} />
</div>

  );
}

export default Body;
