import '../App.css';
import { useState } from 'react';

function App() {
  const [population, setPopulation] = useState([]);

  async function fetchPopulation() {
    const response = await fetch('https://datausa.io/api/data?drilldowns=Nation&measures=Population');
    const data = await response.json();
    setPopulation(data.data);
  }

  const renderPopulation = population.map((item, index) => (
    <div key={index}>
      <span>Country: {item.Nation}</span>
      <span>Population: {item.Population}</span>
      <span>Year: {item.Year}</span>
    </div>
  ));

  function filterYear(year) {
    const filteredPopulation = population.filter(item => item.Year === year);
    setPopulation(filteredPopulation);
  }

  return (
    <div className="App">
      <h1>US Population</h1>
      <button onClick={fetchPopulation}>Click</button>
      <div>
        <select onChange={(e) => filterYear(e.target.value)}>
          <option value="">All</option>
          <option value="2022">2022</option>
          <option value="2021">2021</option>
          <option value="2020">2020</option>
        </select>
      </div>
      <div>{renderPopulation}</div>
    </div>
  );
}

export default App;
