import '../App.css';
import { useState, useEffect } from 'react';
import { Funnel } from '@nivo/funnel';



function App() {
  const [population, setPopulation] = useState([]);
  const [yearFilter, setYearFilter] = useState(null);

  const data = [
    { stage: 'Visited website', count: 2000 },
    { stage: 'Added product to cart', count: 1500 },
    { stage: 'Started checkout', count: 1000 },
    { stage: 'Completed checkout', count: 500 },
  ];

  async function fetchPopulation() {
    const response = await fetch('https://datausa.io/api/data?drilldowns=Nation&measures=Population');
    const data = await response.json();
    setPopulation(data.data);
  }

  useEffect(() => {
    fetchPopulation();
  }, []);

  function handleYearFilterChange(event) {
    setYearFilter(parseInt(event.target.value));
  }

  const renderPopulation = population
    .filter(item => yearFilter ? new Date(item.Year).getFullYear() >= (new Date().getFullYear() - yearFilter) : true)
    .map((item, index) => (
      <div key={index}>
        <span>Country: {item.Nation}</span>
        <span>Population: {item.Population}</span>
        <span>Year: {item.Year}</span>
      </div>
    ));

  return (
    <div className="App">
      <h1>USA Population</h1>
      <label>
        Filter by year:
        <select defaultValue="" onChange={handleYearFilterChange}>
          <option value={null}>All</option>
          <option value={3}>Last 3 years</option>
          <option value={5}>Last 5 years</option>
          <option value={10}>Last 10 years</option>
        </select>
      </label>
      <div>{renderPopulation}</div>
      <Funnel
    width={600}
    height={400}
    data={data}
    margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
    valueFormat=".0%"
    label={({ stage }) => `${stage}`}
    colors={{ scheme: 'spectral' }}
    borderWidth={2}
    borderColor={{ from: 'color', modifiers: [['darker', 0.2]] }}
    animate={true}
  />
    </div>
  );
}

export default App;
