import '../App.css';
import { useState, useEffect } from 'react';

function App() {
  const [population, setPopulation] = useState([]);
  const [selectedYear, setSelectedYear] = useState(null);

  async function fetchPopulation() {
    const response = await fetch('https://datausa.io/api/data?drilldowns=Nation&measures=Population');
    const data = await response.json();
    setPopulation(data.data);
  }

  const handleYearChange = (event) => {
    setSelectedYear(event.target.value);
  };

  const renderPopulation = population
    .filter((item) => {
      if (!selectedYear) {
        return true;
      }

      const currentYear = new Date().getFullYear();
      const selectedYearStart = currentYear - selectedYear + 1;

      return parseInt(item.Year) >= selectedYearStart;
    })
    .map((item, index) => (
      <div key={index}>
        <span>{item.Nation}: </span>
        <span>{item.Population}</span>
      </div>
    ));

  useEffect(() => {
    fetchPopulation();
  }, []);

  return (
    <div className="App">
      <h1>US Population</h1>
      <div>
        <label htmlFor="year-select">Select a year:</label>
        <select id="year-select" onChange={handleYearChange}>
          <option value="">All years</option>
          <option value="3">Last 3 years</option>
          <option value="5">Last 5 years</option>
          <option value="10">Last 10 years</option>
        </select>
      </div>
      <div>
        {renderPopulation.filter((item) => {
          if (!selectedYear) {
            return true;
          }

          return item.props.children[1].includes(selectedYear);
        })}
      </div>
    </div>
  );
}

export default App;
