import "../App.css";
import { useState, useEffect } from "react";
import Chart from "./Chart/Chart";

function Body() {
  const [population, setPopulation] = useState([]);
  const [yearFilter, setYearFilter] = useState(null);

  /**
   * Fetch the data from the Api
   */
  async function fetchPopulation() {
    const response = await fetch(
      "https://datausa.io/api/data?drilldowns=Nation&measures=Population"
    );
    const data = await response.json();
    setPopulation(data.data);
  }

  useEffect(() => {
    fetchPopulation();
  }, []);

  /**
   * Filter the population according to the select length of years
   * @param {*} years 
   */
  function handleYearFilterChange(years) {
    setYearFilter(years);
  }

  return (
    <div className="bg-gray-100 min-h-screen flex flex-col justify-center items-center">
  <h1 className="text-2xl font-bold mb-8">USA Population</h1>
  <div className="flex flex-wrap justify-center items-center mb-8">
    <button 
      onClick={() => handleYearFilterChange(null)} 
      className={`bg-${yearFilter === null ? 'fe7750' : 'ffd050'} rounded-lg px-4 py-2 m-2 bg-yellow-300 focus:outline-none focus:bg-gray-300 transition-colors`}
    >
      All
    </button>
    <button 
      onClick={() => handleYearFilterChange(3)} 
      className={`bg-${yearFilter === 3 ? 'fe7750' : 'ffd050'} rounded-lg px-4 py-2 m-2 focus:outline-none focus:bg-gray-300 transition-colors`}
    >
      Last 3 years
    </button>
    <button 
      onClick={() => handleYearFilterChange(5)} 
      className={`bg-${yearFilter === 5 ? 'fe7750' : 'ffd050'} rounded-lg px-4 py-2 m-2 focus:outline-none focus:bg-gray-300 transition-colors`}
    >
      Last 5 years
    </button>
    <button 
      onClick={() => handleYearFilterChange(10)} 
      className={`bg-${yearFilter === 10 ? 'fe7750' : 'ffd050'} rounded-lg px-4 py-2 m-2 focus:outline-none focus:bg-gray-300 transition-colors`}
    >
      Last 10 years
    </button>
  </div>
  <div className="flex justify-center items-center flex-1">
    <Chart data={population} yearFilter={yearFilter} />
  </div>
</div>


  );
}

export default Body;
