/* This file contains D3.js functions that help building the chart */

/**
 * Draw the chart X axis
 * @param {*} svg 
 * @param {*} chartHeight 
 * @param {*} xAxis 
 */
export function drawXAxis(svg, chartHeight, xAxis) {
  svg.select(".x-axis").attr("transform", `translate(0, ${chartHeight})`).call(xAxis);
}

/**
 * Draw the chart Y axis
 * @param {*} svg 
 * @param {*} yAxis 
 */
export function drawYAxis(svg, yAxis) {
  svg.select(".y-axis").call(yAxis);
}

/**
 * Draw the chart bars
 * @param {*} svg 
 * @param {*} data 
 * @param {*} x 
 * @param {*} y 
 * @param {*} chartHeight 
 */
export function drawBars(svg, data, x, y, chartHeight) {
  const bars = svg.selectAll(".bar").data(data, d => d.Year);
  bars.enter().append("rect").attr("class", "bar").attr("x", d => x(new Date(d.Year).getFullYear())).attr("y", chartHeight).attr("width", x.bandwidth()).attr("height", 0).style("opacity", 0).merge(bars).transition().duration(900).attr("x", d => x(new Date(d.Year).getFullYear())).attr("y", d => y(d.Population)).attr("width", x.bandwidth()).attr("height", d => chartHeight - y(d.Population)).style("opacity", 1);
  bars.exit().transition().duration(900).attr("y", chartHeight).attr("height", 0).style("opacity", 0).remove();
}
