import React, { useRef, useEffect } from "react";
import * as d3 from "d3";

function Chart({ data }) {
  const chartRef = useRef();

  useEffect(() => {
    const svg = d3.select(chartRef.current);

    const margin = { top: 20, right: 20, bottom: 50, left: 70 };
    const chartWidth = +svg.attr("width") - margin.left - margin.right;
    const chartHeight = +svg.attr("height") - margin.top - margin.bottom;

    const filteredData = data.filter((d) => d.Year === 2019);

    const x = d3
      .scaleBand()
      .range([0, chartWidth])
      .domain(filteredData.map((d) => d.Country))
      .padding(0.2);

    const y = d3
      .scaleLinear()
      .range([chartHeight, 0])
      .domain([0, d3.max(filteredData, (d) => d.Population)]);

    const yAxis = d3.axisLeft(y).ticks(5).tickFormat(d3.format(".2s"));

    svg
      .append("g")
      .attr("transform", `translate(${margin.left}, ${chartHeight + margin.top})`)
      .call(d3.axisBottom(x))
      .selectAll("text")
      .style("text-anchor", "end")
      .attr("transform", "rotate(-45)");

    svg
      .append("g")
      .attr("transform", `translate(${margin.left}, ${margin.top})`)
      .call(yAxis);

    svg
      .append("g")
      .attr("transform", `translate(${margin.left}, ${margin.top})`)
      .selectAll()
      .data(filteredData)
      .enter()
      .append("rect")
      .attr("x", (d) => x(d.Country))
      .attr("y", (d) => y(d.Population))
      .attr("height", (d) => chartHeight - y(d.Population))
      .attr("width", x.bandwidth())
      .style("fill", "steelblue");
  }, [data]);

  return (
    <div className="chart">
      <svg ref={chartRef} width="800" height="400"></svg>
    </div>
  );
}

export default Chart;
