import "../App.css";
import { useState, useEffect } from "react";
import Chart from "./Chart/Chart";

function Body() {
  const [population, setPopulation] = useState([]);
  const [yearFilter, setYearFilter] = useState(null);
  const [userData, setUserData] = useState({
    labels: [],
    datasets: [
      {
        label: "Population",
      data: [],
      backgroundColor: ["black"],
      borderColor: "gray",
      borderWidth: 2,
      pointBackgroundColor: "black",
      pointBorderColor: "#FD7751",
      pointBorderWidth: 4,
      pointHoverBackgroundColor: "#FFD866",
      pointHoverBorderColor: "#FFD866",
      pointHoverRadius: 5,
      pointRadius: 3
      },
    ],
  });

  async function fetchPopulation() {
    const response = await fetch(
      "https://datausa.io/api/data?drilldowns=Nation&measures=Population"
    );
    const data = await response.json();
    setPopulation(data.data);
  }

  useEffect(() => {
    fetchPopulation();
  }, []);

  useEffect(() => {
    if (population.length > 0) {
      const filteredPopulation = population.filter(
        (item) =>
          !yearFilter ||
          new Date(item.Year).getFullYear() >=
            new Date().getFullYear() - yearFilter
      );
      const labels = filteredPopulation.map((data) => data.Year);
      const data = filteredPopulation.map((data) => data.Population);

      setUserData((prevState) => {
        return {
          ...prevState,
          labels: labels,
          datasets: [
            {
              ...prevState.datasets[0],
              data: data,
            },
          ],
        };
      });
    }
  }, [yearFilter, population]);

  function handleYearFilterChange(event) {
    setYearFilter(parseInt(event.target.value));
  }

  return (
    <div className="flex flex-col items-center justify-center">
      <div className="relative flex flex-col justify-center items-center flex-grow w-full">
        <Chart chartData={userData} />
        <div className="flex flex-col items-center justify-center mt-12">
          <label className="text-lg font-bold mr-2 text-gray-800">
            Filter by year:
          </label>
          <select
            defaultValue={10}
            onChange={handleYearFilterChange}
            className="p-2 text-lg rounded-md border-2 border-black bg-orange-500 text-white"
          >
            <option value={3}>Last 3 years</option>
            <option value={5}>Last 5 years</option>
            <option value={10}>Last 10 years</option>
          </select>
        </div>
      </div>
    </div>
  );
}

export default Body;
