import "../App.css";
import { useState, useEffect } from "react";
import BarChart from "./Chart/BarChart";

function Body() {
  const [population, setPopulation] = useState([]);
  const [yearFilter, setYearFilter] = useState(null);
  const [userData, setUserData] = useState({
    labels: [],
    datasets: [
      {
        label: "Population",
        data: [],
      },
    ],
  });

  async function fetchPopulation() {
    const response = await fetch(
      "https://datausa.io/api/data?drilldowns=Nation&measures=Population"
    );
    const data = await response.json();
    setPopulation(data.data);
  }

  useEffect(() => {
    fetchPopulation();
  }, []);

  useEffect(() => {
    if (population.length > 0) {
      const filteredPopulation = population.filter(
        (item) =>
          !yearFilter ||
          new Date(item.Year).getFullYear() >=
            new Date().getFullYear() - yearFilter
      );
      const labels = filteredPopulation.map((data) => data.Year);
      const data = filteredPopulation.map((data) => data.Population);

      setUserData((prevState) => {
        return {
          ...prevState,
          labels: labels,
          datasets: [
            {
              ...prevState.datasets[0],
              data: data,
            },
          ],
        };
      });
    }
  }, [yearFilter, population]);

  function handleYearFilterChange(event) {
    setYearFilter(parseInt(event.target.value));
  }

  return (
    <div className="App">
      <h1>USA Population</h1>
      <label>
        Filter by year:
        <select defaultValue="" onChange={handleYearFilterChange}>
          <option value={null}>All</option>
          <option value={3}>Last 3 years</option>
          <option value={5}>Last 5 years</option>
          <option value={10}>Last 10 years</option>
        </select>
      </label>
      <BarChart chartData={userData} />
    </div>
  );
}

export default Body;
