import { useEffect, useRef } from "react";
import * as d3 from "d3";

function Chart({ data, yearFilter }) {
  const svgRef = useRef(null);

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    const width = svg.attr("width");
    const height = svg.attr("height");

    const margin = { top: 20, right: 20, bottom: 30, left: 40 };
    const chartWidth = width - margin.left - margin.right;
    const chartHeight = height - margin.top - margin.bottom;

    const allYears = data.map((item) => new Date(item.Year).getFullYear());
    const filteredData = data.filter(
      (item) =>
        yearFilter
          ? new Date(item.Year).getFullYear() >=
            new Date().getFullYear() - yearFilter
          : true
    );

    const x = d3
    .scaleBand()
    .range([0, chartWidth])
    .padding(0.1)
    .domain(allYears);

  const y = d3
    .scaleLinear()
    .range([chartHeight, 0])
    .domain([0, d3.max(filteredData, (d) => d.Population)]);

  const xAxis = d3.axisBottom(x);
  const yAxis = d3.axisLeft(y);

  svg.select(".x-axis")
    .attr("transform", `translate(0, ${chartHeight})`)
    .transition()
    .duration(900)
    .call(xAxis);

  svg.select(".y-axis")
    .transition()
    .duration(900)
    .call(yAxis);

    svg.selectAll(".bar")
      .data(filteredData, (d) => d.Year)
      .join(
        (enter) =>
          enter
            .append("rect")
            .attr("class", "bar")
            .attr("x", (d) => x(new Date(d.Year).getFullYear()))
            .attr("y", chartHeight)
            .attr("width", x.bandwidth())
            .attr("height", 0)
            .style("opacity", 0)
            .call((enter) =>
              enter
                .transition()
                .duration(900)
                .attr("y", (d) => y(d.Population))
                .attr("height", (d) => chartHeight - y(d.Population))
                .style("opacity", 1)
            ),
        (update) =>
          update.call((update) =>
            update
              .transition()
              .duration(900)
              .attr("x", (d) => x(new Date(d.Year).getFullYear()))
              .attr("y", (d) => y(d.Population))
              .attr("width", x.bandwidth())
              .attr("height", (d) => chartHeight - y(d.Population))
          ),
        (exit) =>
          exit.call((exit) =>
            exit
              /* .transition()
              .duration(900) */
              .attr("y", chartHeight)
              .attr("height", 0)
              .style("opacity", 0)
              .remove()
          )
      )
      .style("fill", (d) =>
        new Date(d.Year).getFullYear() === 2020 ? "red" : "steelblue"
      );
  }, [data, yearFilter]);

  return (
    <svg ref={svgRef} width={500} height={300}>
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  );
}

export default Chart;
